import { TOOGLE_HIDDEN_CART } from '../actions';

export const ebornReducer = (state= {}, action) => {
    switch(action.type) {
        case TOOGLE_HIDDEN_CART:
            return {
                ...state,
                hiddenCart: !hiddenCart
            }

        default: 
            return state;
    }
}
