        }
    };

    return fetch(url,filters)
        .then(response => {
            console.log('-----',response)
            return {error: false, data: response.json() }
        })
        .catch(err => {
            return {error: true, data:[], message:"Error del servidor"}
        })
}

 */
export const getProductsApi = async ()=> {
     const url = `${basePath}/${apiVersion}/get-products`;

    const filters = {
        method: "GET",
        headers: {
            "Content-Type": "application/json"
        }
    };

    try {
        const response = await axios.get(url,filters)
        return  {error: false, data: response.json() }
    } catch(error) {
        return {error: true, data:[], message:"Error del servidor"}
    }

}
